sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    /*
        //rightNow stores the current date and time
        var rightNow = new GlideDateTime();
        //Create a GlideDateTime object for When needed date field
        var whenNeeded = new GlideDateTime(current.u_when_needed);

        //If When needed date is less than the current date, abort creating the NeedIt record.
        //Meaning, the record will not be saved in the database
        //Outputs an error message at the top of the form
        if (whenNeeded < rightNow) {
            //Abort the database operation Insert 
            current.setAbortAction(true);
            gs.addErrorMessage('When needed date cannot be in the past.  Your request has not been saved to the database.');
        }

        //Get the current user's date [Current date in the form]
        var today = rightNow.getLocalDate();
        var whenNeededDate = whenNeeded.getLocalDate();

        if (today == whenNeededDate) {
            //Abort the database operation Insert 
            current.setAbortAction(true);
            gs.addErrorMessage('You cannot create NeedIt requests for today.');
        }
    */

	//Retrieve the value from the Advanced Notice Days property
	var daysLeadTime = GlideApplicationProperty.getValue('x_58872_needit.advancedNoticeDays');

	var whenNeeded = new GlideDateTime(current.u_when_needed.getDisplayValue());
	//Add a specified number of days to the GlideDateTime object. A negative parameter subtract days
	whenNeeded.addDaysUTC(-daysLeadTime);
	var comparisonDate = whenNeeded.getDate();

    //Instantiate the NeedItUtils class
    var niUtils = new NeedItUtils();
    //Invoke the isDatePast function and pass the When needed date value to Script Include
    //Store the return value to isPast variable
    var isPast = niUtils.isDatePast(comparisonDate);

    //Validate the return value
    if (isPast == true) {
        //Abort the database operation Insert 
        current.setAbortAction(true);
        gs.addErrorMessage('When needed date cannot be in the past.  Your request has not been saved to the database.');
    }

    //Invoke the isDateToday function and pass the When needed date value to Script Include
    //Store the return value to isPast variable
    var isToday = niUtils.isDateToday(current.u_when_needed);

    //Validate the return value
    if (isToday == true) {
        //Abort the database operation Insert 
        current.setAbortAction(true);
        gs.addErrorMessage('You cannot create NeedIt requests for today.');
    }


})(current, previous);
