sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    //To execute immediately the onChange Client Script when form loads, remove the isLoading from the if statement
    //Since the Request type field has a default value, that default value will be set as the newValue. Hence, run the remaining code since it does not satisfy the if statement below
    if (newValue === '') {
        return;
    }

    //Clear all the choices from the What needed field choice list
    g_form.clearOptions('u_what_needed');

    //If the value of the Request type field is hr, add one hr choice and other to the What needed field choice list
    if (newValue == 'hr') {
        g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1');
        g_form.addOption('u_what_needed', 'other', 'Other');
    }

    //If the value of the Request type field is facilities, add one facilities choice and other to the What needed field choice list
    if (newValue == 'facilities') {
        g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
        g_form.addOption('u_what_needed', 'other', 'Other');
    }

    //If the value of the Request type field is legal, add one legal choice and other to the What needed field choice list
    if (newValue == 'legal') {
        g_form.addOption('u_what_needed', 'legal1', 'Legal 1');
        g_form.addOption('u_what_needed', 'other', 'Other');
    }

	//If the value of the Request type field is TPS Report, add other to the What needed field choice list
	if (newValue == 'TPS Report'){
		g_form.addOption('u_what_needed', 'other', 'Other');
	}

    //If form is loading and it is an existing record, set the What needed value to the value from the database before it was loaded
    //The above code still executes to filter out the What needed choices based on the value of the Request type
    if (isLoading && !g_form.isNewRecord()) {
        //g_form.addInfoMessage('what needed value = ' + g_form.getValue('u_what_needed'));
        g_form.setValue('u_what_needed', g_form.getValue('u_what_needed'));
    }

}
