sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var rightNow = new GlideDateTime();

    // Construct a GlideRecord query to find all NeedIt Task records with Due date
    // field values in the past for the same Assigned to.  The records cannot be in the
    // Closed state.
    var gr = new GlideRecord('x_58872_needit_needit_task');
    gr.addQuery('assigned_to', current.assigned_to);
    gr.addQuery('due_date', '<=', rightNow);
    gr.addQuery('state', '!=', 3);
    gr.query();

	//If the Assigned to has more than 1 overdue NeedIt task record, print the message to the notification. Otherwise, do not print.
    var count = gr.getRowCount();
    if (count > 1) {
        template.print("In addition to this overdue NeedIt Task, you have " + (gr.getRowCount() - 1) + " more overdue NeedIt Tasks.");
    }

})(current, template, email, email_action, event);
