sys_script_include.script:
function ValidateEmailAddress(email){
	// Use JavaScript coercion to guarantee email is a string
	email = email + '';

	if(email.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	} else {
		return false;
	}
}
