sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    //Get value from the user_name path parameter passed in the URL
    var requestNeedItUser = request.pathParams.user_name;
    //Get value of the ni_query query parameter passed in the URL
    var requestNeedItQuery = request.queryParams.ni_query;

    //Query the sys_user table to get the user record for the user passed in the user_name path parameter
    var gr = new GlideRecord('sys_user');
    gr.get('user_name', requestNeedItUser);

    //Count of active NeedIt records for user (number)
    // Get the count of active NeedIt table records where the user from the
    // user_name path parameter is the Requested for.  The encoded query is from the
    // Query Parameter Associations.  Group by category.
    var userNeedItCount = new GlideAggregate('x_58872_needit_needit');
    userNeedItCount.addAggregate('COUNT');
    userNeedItCount.addQuery('u_requested_for', gr.sys_id);
    userNeedItCount.addEncodedQuery(requestNeedItQuery);
    userNeedItCount.groupBy('u_request_type'); //name of a field to use in grouping the aggregates
    userNeedItCount.query();

    var needitRecs = 0;
    var niUserRecs = [];
    var needitRecsCount = 0;
    while (userNeedItCount.next()) {
        needitRecs = userNeedItCount.getAggregate('COUNT');
        var reqType = userNeedItCount.u_request_type.getDisplayValue(); 
        niUserRecs.push({
            "requestType": reqType,
            "count": needitRecs
        });
        needitRecsCount = parseInt(needitRecsCount) + parseInt(needitRecs);
    }

    //Create a body object.  Add property value pairs to the body.
    var body = {};
    body.totalUserNIRecs = needitRecsCount;
    body.catCounts = niUserRecs;
    body.user = {
        "User name": gr.user_name,
        "User ID": gr.sys_id
    };

    // Send the response object, which is returned to the requestor, to the body object.
    response.setBody(body);

})(request, response);
